import Viewer from './viewer'
import { useState} from 'react'

function App() {    
    const [url, setUrl] = useState('')
    const [searchResults, setSearchResults] = useState<[{ id: number, name: string}] | null>(null)

    const handleSubmit = (event: React.SyntheticEvent<HTMLFormElement>) => {
        event.preventDefault();
        const form = event.currentTarget
        const formElements = form.elements as typeof form.elements & {
            url: {value: string}
          }        
        setUrl(formElements.url.value)
    }

    const handleSearch = async (event: React.SyntheticEvent<HTMLFormElement>) => {
        event.preventDefault();
        const form = event.currentTarget
        const formElements = form.elements as typeof form.elements & {
            searchTerm: {value: string}
          }        
        const response = await fetch(`https://mygene.info/v3/query?q=${formElements.searchTerm.value}&fields=symbol%2C%20name%2C%20entrezgene&species=human&entrezonly=true`)
        const data = await response.json()
        console.log(data.hits)
        setSearchResults(data.hits)
        console.log(searchResults)
    }

    return <>
    <h1>AlphaFold Protein Viewer</h1>
    <form onSubmit={handleSubmit}>
      <label>Submit Alphafold URL:
        <input 
          type="text"
          id="url"
          style={{ width: 600 }}
        />
      </label>
      <input type="submit" />
    </form>
    <form onSubmit={handleSearch}>
      <label>Search protein or gene:
        <input 
          type="text"
          id="searchTerm"
          style={{ width: 600 }}
        />
      </label>
      <input type="submit" />
    </form>
    <br/>
    <div>
        <h1>{ searchResults && searchResults.map((item) => <h2>{item.name}</h2>) }</h1>
    </div>
    <div>
        {url && <Viewer top={250} url={url} ></Viewer>}
    </div>
    </>
}

export default App;
