import Viewer from "./viewer";
import { useEffect, useState } from "react";

interface searchResultsHits {
    entrezgeneID: string;
    symbol: string;
    score: number;
    _id: string;
    name: string;
}

function App() {
    const [url, setUrl] = useState("");
    const [searchResults, setSearchResults] = useState<
        searchResultsHits[] | null
    >(null);

    const handleSubmit = (event: React.SyntheticEvent<HTMLFormElement>) => {
        event.preventDefault();
        const form = event.currentTarget;
        const formElements = form.elements as typeof form.elements & {
            url: { value: string };
        };

        setUrl(formElements.url.value);
    };

    const handleSearch = async (
        event: React.SyntheticEvent<HTMLFormElement>
    ) => {
        event.preventDefault();
        const form = event.currentTarget;
        const formElements = form.elements as typeof form.elements & {
            searchTerm: { value: string };
        };
        const response = await fetch(
            `https://mygene.info/v3/query?q=${formElements.searchTerm.value}&fields=symbol%2C%20name%2C%20entrezgene&species=human&entrezonly=true`
        );
        const data = await response.json();
        setSearchResults(data.hits);
    };

    return (
        <>
            <h1>AlphaFold Protein Viewer</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Submit Alphafold URL:
                    <input type="text" id="url" style={{ width: 600 }} />
                </label>
                <input type="submit" />
            </form>
            <form onSubmit={handleSearch}>
                <label>
                    Search protein or gene:
                    <input type="text" id="searchTerm" style={{ width: 600 }} />
                </label>
                <input type="submit" />
            </form>
            <br />
            <div>
                {searchResults &&
                    searchResults.map((item) => (
                        <>
                            <button>
                                {item.name} {item.symbol}
                            </button>
                            <br />
                        </>
                    ))}
            </div>
            <div>{url && <Viewer left={250} top={250} url={url}></Viewer>}</div>
        </>
    );
}

export default App;
