import React, { useEffect, useRef } from "react";
import { PluginContext } from "molstar/lib/mol-plugin/context";
import { createPlugin } from "molstar/lib/mol-plugin-ui/index";
import "molstar/build/viewer/molstar.css";
import PropTypes from "prop-types";

function Viewer(props: { left: number; top: number; url: string }) {
    const parent = useRef<HTMLDivElement>(null);
    // const plugin = React.useRef<PluginContext | null>();

    useEffect(() => {
        let plugin: PluginContext | undefined = undefined;
        async function init() {
            // plugin.current = createPlugin(parent.current!);
            plugin = createPlugin(parent.current!);
            // const data = await plugin.current.builders.data.download(
            const data = await plugin.builders.data.download(
                { url: props.url },
                { state: { isGhost: true } }
            );
            const trajectory =
                // await plugin.current.builders.structure.parseTrajectory(
                await plugin.builders.structure.parseTrajectory(data, "mmcif");
            // await plugin.current.builders.structure.hierarchy.applyPreset(
            await plugin.builders.structure.hierarchy.applyPreset(
                trajectory,
                "default"
            );
        }
        init();

        // return () => {
        //     plugin.current?.dispose();
        //     plugin.current = undefined;
        // };
    }, [props.url]);

    return (
        <div
            ref={parent}
            style={{
                position: "absolute",
                height: 800,
                width: 1500,
                left: props.left,
                top: props.top,
            }}
        />
    );
}

Viewer.propTypes = {
    left: PropTypes.number.isRequired,
    top: PropTypes.number.isRequired,
    url: PropTypes.string.isRequired,
};

export default Viewer;
