import React, { useEffect } from 'react';
import { PluginContext } from 'molstar/lib/mol-plugin/context';
import { createPlugin } from 'molstar/lib/mol-plugin-ui/index'
import 'molstar/build/viewer/molstar.css'
import { relative } from 'path/posix';

function Viewer() {    
    const parent = React.createRef<HTMLDivElement>();

    useEffect(() => {
        let plugin: PluginContext | undefined = undefined;
        async function init() {
            plugin = createPlugin(parent.current!);
            const data = await plugin.builders.data.download({ url: 'https://alphafold.ebi.ac.uk/files/AF-P38398-F1-model_v1.cif' }, { state: { isGhost: true } });
            const trajectory = await plugin.builders.structure.parseTrajectory(data, "mmcif");
            await plugin.builders.structure.hierarchy.applyPreset(trajectory, 'default');
            plugin.handleResize()
        }
        init();
        return () => { plugin?.dispose(); };
    }, [parent]);


    return <div ref={parent} style={{ width: 640, height: 480}} />;
}

export default Viewer;
